        scene.add_sequence(ks::BackgroundItem(bn::regular_bg_items::school_dormhisao));
        scene.add_sequence(ks::DialogItem("", "I wake up in a strange room."));
        scene.add_sequence(ks::DialogItem("", "Solid morning light shimmers against the light gray ceiling. I had forgotten to draw the curtains closed last night."));
        scene.add_sequence(ks::DialogItem("", "I?"));
        scene.add_sequence(ks::DialogItem("", "This is my room, isn't it?"));
