        scene.add_sequence(ks::DialogItem("mi", "Ahaha~! Sorry, sorry, Shicchan~! Is there something you wanted from me?"));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::shizu, ks::SpriteEventType::None));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::misha, ks::SpriteEventType::None));
        scene.add_sequence(ks::DialogItem("mi", "Oh~… I see! Hm… That's a good question, Hicchan."));
        scene.add_sequence(ks::DialogItem("", "My first thought is that means she doesn't know, which is worrying. Maybe I'm being too negative. Well, anyway, Misha, please don't prove me right."));
