        scene.add_sequence(ks::DialogItem("mi", "There are also school events, like the festival coming up in a few days. Almost every student in the school tends to help out with it, doing whatever."));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::misha, ks::SpriteEventType::None));
        scene.add_sequence(ks::DialogItem("mi", "So~! You actually transferred in at a busy time… maybe you can help out, too~!"));
        scene.add_sequence(ks::DialogItem("hi", "Sure."));
        scene.add_sequence(ks::DialogItem("hi", "What's the festival about?"));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::misha, ks::SpriteEventType::None));
        scene.add_sequence(ks::DialogItem("", "Misha freezes."));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::misha, ks::SpriteEventType::None));
