        scene.add_sequence(ks::DialogItem("", "Too loud. I can see heads turning to look in our direction."));
        scene.add_sequence(ks::DialogItem("hi", "Not so loud…"));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::shizu, ks::SpriteEventType::None));
        scene.add_sequence(ks::DialogItem("shi", "…"));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::misha, ks::SpriteEventType::None));
        scene.add_sequence(ks::DialogItem("mi", "Human beings evolve with each new generation! The ideals and beliefs behind a festival will inevitably change with time!"));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::shizu, ks::SpriteEventType::None));
        scene.add_sequence(ks::DialogItem("shi", "…!"));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::misha, ks::SpriteEventType::None));
        scene.add_sequence(ks::DialogItem("mi", "Now, it's about delicious fried food and amusing little games that you play to win prizes~! Hahahaha~!"));
        scene.add_sequence(ks::DialogItem("", "The teacher clears his throat very loudly, batting his long wooden pointer against his other palm like a baton. He shoots a pointed gaze at us."));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::misha, ks::SpriteEventType::None));
        scene.add_sequence(ks::DialogItem("", "Finally noticing where we are, Misha stifles a yelp and quickly quiets down. Shizune doesn't seem embarrassed at all, though, brushing it off without a care."));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::shizu, ks::SpriteEventType::None));
        scene.add_sequence(ks::DialogItem("shi", "…"));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::misha, ks::SpriteEventType::None));
        scene.add_sequence(ks::DialogItem("mi", "We are in the middle of class, and should start working."));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::misha, ks::SpriteEventType::None));
