        scene.add_sequence(ks::DialogItem("hi", "Yeah, I was thinking about it."));
        scene.add_sequence(ks::DialogItem("", "Misha and Shizune look at each other again."));
        scene.add_sequence(ks::MusicStopItem());
        scene.add_sequence(ks::DialogItem("", "I'm about to ask what they have in mind when something dark flutters in my peripheral vision, catching my attention."));
