        scene.add_sequence(ks::DialogItem("", "It doesn't seem like she was working in any group, and no one seems to notice her but me."));
        scene.add_sequence(ks::DialogItem("", "I glance at the teacher, who's also looking at the dark-haired girl go."));
        scene.add_sequence(ks::DialogItem("", "Why doesn't he say anything?"));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::misha, ks::SpriteEventType::None));
