        scene.add_sequence(ks::DialogItem("", "Do I look as uneasy as I feel?"));
        scene.add_sequence(ks::DialogItem("", "Or was Misha just looking at me looking after the girl who left?"));
        scene.add_sequence(ks::DialogItem("hi", "No, nothing."));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::shizu, ks::SpriteEventType::None));
        scene.add_sequence(ks::DialogItem("shi", "…"));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::misha, ks::SpriteEventType::None));
