        scene.add_sequence(ks::DialogItem("", "'I thought I would go to the library and pick up some books.'"));
        scene.add_sequence(ks::DialogItem("hi", "…Not really."));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::misha, ks::SpriteEventType::None));
        scene.add_sequence(ks::DialogItem("mi", "Do you want to have lunch together then?"));
        scene.add_sequence(ks::DialogItem("hi", "Sure."));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::shizu, ks::SpriteEventType::None));
        scene.add_sequence(ks::DialogItem("shi", "…"));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::misha, ks::SpriteEventType::None));
        scene.add_sequence(ks::DialogItem("mi", "Yay~! Wahahaha~! Okay, Hicchan~! Perfect!"));
        scene.add_sequence(ks::BackgroundItem(bn::regular_bg_items::school_scienceroom));
        scene.add_sequence(ks::DialogItem("", "The rest of class passes uneventfully. The girl with the long hair never came back."));
        scene.add_sequence(ks::DialogItem("", "Before I have the time to put any more thought into where she could have gone, the teacher informs us that it's time to stop working."));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::shizu, ks::SpriteEventType::None));
        scene.add_sequence(ks::DialogItem("", "Shizune looks more than a little annoyed that we only just barely managed to finish all our work on time. I'm just glad we finished it at all; it's not a contest or anything."));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::shizu, ks::SpriteEventType::None));
        scene.add_sequence(ks::DialogItem("shi", "…"));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::shizu, ks::SpriteEventType::None));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::bg, ks::SpriteEventType::None));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::misha, ks::SpriteEventType::None));
        scene.add_sequence(ks::MusicNotFoundItem());
        scene.add_sequence(ks::DialogItem("mi", "Yes it is, Hicchan!"));
        scene.add_sequence(ks::DialogItem("hi", "Impossible."));
        scene.add_sequence(ks::DialogItem("mi", "Really?"));
        scene.add_sequence(ks::DialogItem("hi", "Really."));
        scene.add_sequence(ks::DialogItem("", "I've noticed this before, but it's kind of funny how Misha is always moving her hands and signing not only everything she says, but what anyone else is saying at any given time."));
        scene.add_sequence(ks::DialogItem("", "Obviously, it must be so Shizune can understand it. Her eyes dart back and forth between Misha's hands and me."));
        scene.add_sequence(ks::DialogItem("", "I don't know who I'm supposed to be looking at. I'm talking to Misha, but that might be wrong; maybe I should face Shizune. I'm used to looking in the direction of the person whose voice I'm hearing, but really…"));
        scene.add_sequence(ks::DialogItem("", "Shizune can't hear me, but it would be disrespectful to talk to her only through Misha. Then again, isn't that what she's doing? No, she's at least looking at me. This is all very confusing and will take some time to get used to."));
        scene.add_sequence(ks::DialogItem("hi", "It's not a contest, because contests are competitions over a prize. If there's no prize on the line, it's not really a contest."));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::shizu, ks::SpriteEventType::None));
        scene.add_sequence(ks::DialogItem("", "Shizune's eyes flash dangerously with a competitive glare. She stares at me, as if surprised that I'm challenging her. I think maybe this is a contest to her."));
        scene.add_sequence(ks::DialogItem("", "I never noticed before how dark and blue her eyes are; it's truly an alluring gaze."));
        scene.add_sequence(ks::DialogItem("shi", "…"));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::misha, ks::SpriteEventType::None));
        scene.add_sequence(ks::DialogItem("mi", "Are you sure, Hicchan?"));
        scene.add_sequence(ks::DialogItem("hi", "Very sure."));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::shizu, ks::SpriteEventType::None));
        scene.add_sequence(ks::DialogItem("shi", "…"));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::misha, ks::SpriteEventType::None));
        scene.add_sequence(ks::DialogItem("mi", "Hahaha! You're wrong, Hicchan. Because~!, I don't want to be the slowest one in the class. Therefore, what's on the line is my confidence in my abilities, and the prize is the satisfaction of proving them."));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::misha, ks::SpriteEventType::None));
        scene.add_sequence(ks::DialogItem("mi", "Wahahahaha~!"));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::shizu, ks::SpriteEventType::None));
        scene.add_sequence(ks::DialogItem("", "Shizune pushes her glasses up the bridge of her nose in a very matter-of-fact way."));
        scene.add_sequence(ks::DialogItem("", "I'd argue more, but the bell rings, and she quickly gets up and picks up her bag, looking at me expectantly."));
        scene.add_sequence(ks::BackgroundItem(bn::regular_bg_items::school_hallway3));
        scene.add_sequence(ks::DialogItem("", "I had almost forgotten that I was supposed to have lunch with them."));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::misha, ks::SpriteEventType::None));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::shizu, ks::SpriteEventType::None));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::misha, ks::SpriteEventType::None));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::shizu, ks::SpriteEventType::None));
