        scene.add_sequence(ks::DialogItem("shi", "…"));
        scene.add_sequence(ks::DialogItem("mi", "Ah, okay! But, more to the point, Hicchan, does this mean that you don't have anything already in mind?"));
        scene.add_sequence(ks::DialogItem("hi", "Not really."));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::shizu, ks::SpriteEventType::None));
        scene.add_sequence(ks::DialogItem("shi", "…"));
