        scene.add_sequence(ks::DialogItem("mi", "Actually, Shicchan is the president."));
        scene.add_sequence(ks::DialogItem("", "Hm…"));
        scene.add_sequence(ks::DialogItem("", "I'm starting to get the suspicion that Shizune and Misha might not exactly be the most unbiased people to talk about this with."));
        scene.add_sequence(ks::DialogItem("", "As if reading my mind, Shizune quickly adjusts her glasses and signs something to Misha."));
