        scene.add_sequence(ks::DialogItem("shi", "…"));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::misha, ks::SpriteEventType::None));
        scene.add_sequence(ks::DialogItem("mi", "Hahaha~! Of course, we're not trying to get you to join just because we would obviously benefit from you joining the Student Council and therefore have an incentive to try and get you to—"));
        scene.add_sequence(ks::DialogItem("hi", "So, you're admitting that—"));
        // scene.add_sequence(ks::SpriteItem(bn::sprite_items::shizu, ks::SpriteEventType::None));
        scene.add_sequence(ks::DialogItem("shi", "…"));
