                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::tearoom_hisao, ks::SpriteEventType::None));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::tearoom_lilly, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("", "Я аккуратно беру чашку и делаю глоток. На самом деле я предпочитаю кофе, но сейчас не лучший момент упоминать об этом."));
                scene.add_sequence(ks::DialogItem("", "Тем более что запах довольно приятный. Не думаю, что будет сложно выпить всю чашку до дна."));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::tearoom_hisao, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("hi", "Спасибо, Сато. Очень вкусный чай."));
