                scene.add_sequence(ks::DialogItem("hi", "Ты здесь часто пьёшь чай? Место действительно очень хорошее."));
                scene.add_sequence(ks::DialogItem("", "Наверное, для неё эта комната – как мой уголок, где я любил перекусывать, позади здания в старой школе."));
