                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::tearoom_hisao, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("li", "Я часто прихожу сюда во время ланча. Мои обязанности старосты класса не оставляют времени на кружки и прочее, так что мы с подругой используем эту комнату для чаепитий."));
                scene.add_sequence(ks::DialogItem("", "Староста, значит?"));
                scene.add_sequence(ks::DialogItem("", "Она и Сидзунэ – полные противоположности. Сидзунэ резкая и энергичная, Лилли же – спокойная и расслабленная, чуть отстранённая."));
                scene.add_sequence(ks::DialogItem("", "Кстати, думаю, от неё можно услышать более непредвзятое мнение о школьных кружках."));
