                scene.add_sequence(ks::DialogItem("", "Ну, если только навык ориентирования на местности меня не подведёт.{w} А такое нередко случается."));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::tearoom_lilly, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("li", "Это нисколько меня не затруднит, я как раз собиралась поговорить с библиотекарем. Могу представить тебя ей."));
                scene.add_sequence(ks::DialogItem("", "Всё лучше и лучше. От такого предложения сложно отказаться."));
                scene.add_sequence(ks::DialogItem("hi", "Было бы здорово, раз ты не против. Спасибо."));
                scene.add_sequence(ks::BackgroundItem(bn::regular_bg_items::school_miyagi_ss));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::lilly, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("", "Она встаёт и, взяв прислонённую к её портфелю трость, следует за мной."));
                scene.add_sequence(ks::DialogItem("", "Её трость гораздо тоньше и длиннее, чем у моего одноклассника. Наверное, потому что она использует её для того, чтобы ориентироваться в пространстве, а не для опоры при ходьбе."));
                scene.add_sequence(ks::DialogItem("", "Направляясь в библиотеку, мы выходим из комнаты и оказываемся в пустом коридоре."));
                scene.add_sequence(ks::MusicStopItem());
                scene.add_sequence(ks::BackgroundItem(bn::regular_bg_items::school_hallway2));
                scene.add_sequence(ks::DialogItem("", "Я иду не торопясь, подстраиваясь под её неспешные шаги."));
                scene.add_sequence(ks::DialogItem("", "Спустя некоторое время мы подходим к двери уютно выглядящего помещения, расположенного посередине этажа, а не в одном из крыльев здания."));
                scene.add_sequence(ks::DialogItem("hi", "Дамы вперёд."));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::lilly, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("", "Она признательно улыбается в ответ на этот жест и заходит внутрь."));
                scene.add_sequence(ks::BackgroundItem(bn::regular_bg_items::school_library_ss));
                scene.add_sequence(ks::MusicItem(bn::music_items::music_happiness));
                scene.add_sequence(ks::DialogItem("", "Слева от входа располагается стол библиотекаря, книжные полки ровными рядами стоят по правую руку."));
                scene.add_sequence(ks::DialogItem("", "По сравнению с библиотекой моей прежней школы, эта кажется мне просто огромной. Запах старых книг придаёт этому месту, можно сказать, дух старины."));
                scene.add_sequence(ks::DialogItem("", "Читателей в этот час здесь почти нет: все гуляют или отдыхают в общежитии."));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::lilly, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("li", "Юко, ты здесь?"));
                scene.add_sequence(ks::DialogItem("", "Она задаёт этот вопрос в пустоту, потому что библиотекаря, кажется, нет на месте, а Лилли этого, конечно, не видит."));
                scene.add_sequence(ks::DialogItem("", "Но, к моему удивлению, ответ всё же следует."));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::lilly, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("", "Из-под стола библиотекаря раздаётся звук удара, а следом за ним тихий стон."));
