                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::lilly, ks::SpriteEventType::None));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::bg, ks::SpriteEventType::None));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::yuuko, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("", "Голос принадлежит девушке, которая уже выбралась из-под стола и теперь стоит перед нами, неуклюже потирая затылок."));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::yuuko, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("yu_", "Привет, Лилли. Чем могу помочь?"));
                scene.add_sequence(ks::DialogItem("", "Она старается, чтобы её голос звучал как ни в чём не бывало."));
                scene.add_sequence(ks::DialogItem("li", "Добрый день. Что произошло? Я слышала какой-то странный звук."));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::yuuko, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("yu_", "Ничего, я всего лишь немного ушиблась."));
                scene.add_sequence(ks::DialogItem("yu_", "Видишь ли, я уронила ластик под стол, а пока искала, упал и карандаш, я стала искать и его, а потом ты пришла и позвала меня так неожиданно…"));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::lilly, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("li", "Ты в порядке? Прошу прощения, я не нарочно—"));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::yuuko, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("yu_", "Всё нормально! Я в полном порядке – не переживай за меня."));
