                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::lilly, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("yu_", "Это ерунда, со мной и похуже бывало."));
                scene.add_sequence(ks::DialogItem("", "Она спешно прерывает извинения Лилли, словно отвергает саму возможность, что шишка на голове может её хоть сколько-нибудь беспокоить."));
                scene.add_sequence(ks::DialogItem("yu_", "Да… бывало и похуже, хе-хе-хе…"));
                scene.add_sequence(ks::DialogItem("", "Она нервно перебирает пальцами, а с лица Лилли никак не сходит встревоженное выражение; затем Юко без видимой причины начинает перекладывать с места на место какие-то бумаги."));
                scene.add_sequence(ks::DialogItem("", "Юко немного ниже Лилли, на покрытом веснушками носике небрежно сидят очки. Её облик идеально соответствует стереотипам о внешнем виде библиотекаря."));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::yuuko, ks::SpriteEventType::None));
