                scene.add_sequence(ks::DialogItem("", "Внезапно прервав своё ликование, она обращает внимание на меня, отчасти – я почти уверен – чтобы сменить тему разговора, и застывает на месте."));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::yuuko, ks::SpriteEventType::None));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::lilly, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("yu_", "Ой, простите, я вас не сразу заметила!"));
                scene.add_sequence(ks::DialogItem("yu_", "Вы хотели взять книгу? Или вернуть? Извините! Извините!"));
