                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::lilly, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("li", "Он пришёл со мной. Юко, это Хисао, новый ученик. Хисао, это Юко, школьный библиотекарь."));
