                scene.add_sequence(ks::DialogItem("li", "Юко часто заказывает для меня книги на Брайле."));
                scene.add_sequence(ks::DialogItem("li", "Ты не расскажешь Хисао вкратце о библиотеке?"));
                scene.add_sequence(ks::DialogItem("", "Невинное предложение Лилли повергает библиотекаря в панику."));
