                scene.add_sequence(ks::DialogItem("yu", "Я… умоляю, Лилли, я не могу. Я не знаю, что именно его интересует. Это слишком большая ответственность."));
                scene.add_sequence(ks::DialogItem("", "Я не понимаю, каким образом это может считаться ответственностью, но она выглядит так, будто скорее сгорит на месте, чем покажет мне полки с ранобэ."));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::lilly, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("li", "Но…"));
