                scene.add_sequence(ks::DialogItem("", "Почему-то мне неловко говорить об этом малознакомым людям, пусть они и настолько дружелюбны."));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::tearoom_hisaoe, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("hi", "Ерунда, просто опешил от неожиданности."));
