                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::tearoom_hanae, ks::SpriteEventType::None));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::tearoom_hisaoe, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("", "Судя по выражению их лиц, они не верят мне. Но всё равно не настаивают. Должно быть, из уважения к моей частной жизни."));
                scene.add_sequence(ks::DialogItem("", "Полагаю, это одно из местных негласных правил: «не спрашивай». Даже если проблема очевидна, как, например, у Ханако, всё равно есть рамки и история её возникновения находится за ними."));
