                scene.add_sequence(ks::DialogItem("", "Наклоняясь за тростью, Лилли кивает нам на прощание, и мы с Ханако выходим первыми."));
                scene.add_sequence(ks::BackgroundItem(bn::regular_bg_items::school_hallway3));
                scene.add_sequence(ks::DialogItem("", "Мы быстро идём пустыми коридорами к нашим классам."));
                scene.add_sequence(ks::DialogItem("", "Когда мы достигаем дверей класса 3-2, в котором учится Лилли, та поворачивается ко мне."));
