                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::hanako, ks::SpriteEventType::None));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::bg, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("", "На этом наши пути расходятся; Лилли заходит в свой класс, оставив нас с Ханако, которая всё ещё выглядит так, будто хочет сбежать."));
                scene.add_sequence(ks::DialogItem("hi", "Ну что, ты уверена, что хочешь пойти на урок?"));
