                scene.add_sequence(ks::DialogItem("", "Я поспешно здороваюсь с Сидзунэ и Мишей и принимаюсь решать задания с доски. Кажется, над примерами сегодня придётся попотеть."));
                scene.add_sequence(ks::DialogItem("", "Но я не возражаю. Сидзунэ и Миша могут начать требовать от меня ответа по поводу вступления в Школьный совет, хоть и прошёл всего день."));
                scene.add_sequence(ks::DialogItem("", "С них ведь станется. А ответить я пока не смогу, поэтому нынешняя ситуация вполне меня устраивает."));
                scene.add_sequence(ks::DialogItem("", "Минут через десять после начала урока входит Ханако и молча занимает своё место. Никто не смотрит на неё, даже учитель не обращает внимания на её опоздание."));
                scene.add_sequence(ks::DialogItem("", "Он объявляет, что мы снова делимся на группы."));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::shizu, ks::SpriteEventType::None));
