                scene.add_sequence(ks::BackgroundItem(bn::regular_bg_items::school_hallway3));
                scene.add_sequence(ks::DialogItem("", "По соседству с нашим кабинетом располагаются классы 3-1 и 3-2 по правой стороне и 3-4 – по левой, и все двери выглядят совершенно одинаково."));
                scene.add_sequence(ks::DialogItem("", "Дальше по коридору с одинаковыми дверьми находятся кабинеты, для проведения уроков, как я считал, не используемые."));
                scene.add_sequence(ks::DialogItem("", "Наверное, кабинет кружка рисования – один из них."));
