                scene.add_sequence(ks::BackgroundItem(bn::regular_bg_items::school_classroomart));
                scene.add_sequence(ks::DialogItem("", "За ней оказывается классная комната, но либо неубранная, либо вообще неиспользуемая. Я не ошибся дверью?"));
                scene.add_sequence(ks::DialogItem("", "Парты и стулья, покрытые тонким слоем пыли, в беспорядке расставлены по комнате. В углу стоит несколько мольбертов – похоже, я не ошибся."));
                scene.add_sequence(ks::DialogItem("", "Комната залита светом из огромных окон, на партах лежат тени."));
                scene.add_sequence(ks::DialogItem("", "Облачка пыли танцуют в недвижимом воздухе, делая солнечные лучи почти осязаемыми."));
                scene.add_sequence(ks::DialogItem("", "Ради смеха я обращаюсь к пустой комнате."));
                scene.add_sequence(ks::DialogItem("hi", "Есть кто до—{w=.5}{nw}"));
                scene.add_sequence(ks::DialogItem("", "Заметив что-то краем глаза, я останавливаюсь на середине фразы."));
                scene.add_sequence(ks::BackgroundItem(bn::regular_bg_items::rin_eating));
                scene.add_sequence(ks::MusicItem(bn::music_items::music_rin));
                scene.add_sequence(ks::DialogItem("", "На парте сидит коротко стриженая девушка; к моему удивлению, она одета в мужскую школьную форму и держит пальцами ног вилку, на которую нанизан кусочек еды."));
                scene.add_sequence(ks::DialogItem("", "Такой необычный способ принятия пищи вызван очевидным отсутствием у неё рук, но ещё больше меня ошеломляет само её присутствие здесь."));
                scene.add_sequence(ks::DialogItem("", "Как же я её раньше не заметил? Она сидит в углу неподвижно и на первый взгляд кажется предметом обстановки или статуей."));
                scene.add_sequence(ks::DialogItem("", "Я сегодня совсем невнимательный."));
                scene.add_sequence(ks::DialogItem("", "Девушка застыла на месте, уставившись на меня огромными зелёными глазами, как кролик в свете фар."));
                scene.add_sequence(ks::DialogItem("", "Её вилка так и замерла у открытого рта."));
                scene.add_sequence(ks::DialogItem("", "Я смотрю на неё, разинув рот, внезапно вспомнив, что остановился на полуслове, и пытаясь решить, стоит ли заканчивать фразу."));
                scene.add_sequence(ks::DialogItem("", "Мы застыли в тишине, нарушаемой лишь ритмичным тиканьем настенных часов."));
                scene.add_sequence(ks::DialogItem("rin_", "Привет."));
                scene.add_sequence(ks::DialogItem("", "Девушка отправляет вилку в рот и выжидательно на меня смотрит, пережёвывая еду. Я чувствую себя неловко."));
                scene.add_sequence(ks::DialogItem("hi", "М-м… привет. Меня попросили забрать отсюда кое-что. Для фестивальных киосков, в смысле. Я не думал, что тут кто-то есть."));
                scene.add_sequence(ks::DialogItem("rin_", "Никого нет. Именно поэтому я тоже сюда пришла."));
                scene.add_sequence(ks::DialogItem("", "Она накалывает на вилку следующую порцию."));
                scene.add_sequence(ks::DialogItem("hi", "Тогда разве это не значит, что здесь есть ты?"));
                scene.add_sequence(ks::BackgroundItem(bn::regular_bg_items::school_classroomart));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::rin, ks::SpriteEventType::None));
