                scene.add_sequence(ks::DialogItem("rin_", "А ты наблюдательный. Полагаю, что так. А ты кто?"));
                scene.add_sequence(ks::DialogItem("", "Прямолинейная девушка, не правда ли?"));
                scene.add_sequence(ks::DialogItem("hi", "Меня зовут Накай, Хисао Накай. Я только в понедельник перевёлся."));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::rin, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("rin_", "А меня – Рин. Тэдзука Рин. Рин Тэдзука."));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::rin, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("rin", "Руку я тебе не пожму, но теперь мы хотя бы знаем, кто есть кто."));
                scene.add_sequence(ks::DialogItem("rin", "Это здорово."));
                scene.add_sequence(ks::DialogItem("", "По её серьёзной манере разговора трудно определить, шутит ли она насчёт рукопожатия."));
                scene.add_sequence(ks::DialogItem("", "Шутки на эту тему кажутся совсем неуместными, и это меня беспокоит."));
