                scene.add_sequence(ks::DialogItem("", "Пока я пытаюсь выяснить, что уместно, а что нет, девушка теряет ко мне интерес и переводит голодный взгляд на еду."));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::rin, ks::SpriteEventType::None));
