                scene.add_sequence(ks::DialogItem("hi", "Конечно, продолжай. Но… ланч? Учебный день уже кончился."));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::rin, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("rin", "Тогда как бы ты это назвал? Нет специального слова для приёма пищи между ланчем и обедом, ведь так? Меня это тоже беспокоит, но я не знаю, какое слово подобрать."));
                scene.add_sequence(ks::DialogItem("hi", "Не думаю, что между ланчем и обедом следует что-то есть."));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::rin, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("rin", "Но я голодная, и иначе мою восхитительную еду пришлось бы выбросить."));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::rin, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("rin", "У меня тут карри. Очень вкусное."));
                scene.add_sequence(ks::DialogItem("", "Решительным движением Рин снова поднимает зажатую между пальцами ноги вилку и по меньшей мере неучтиво указывает ею прямо на меня."));
                scene.add_sequence(ks::BackgroundItem(bn::regular_bg_items::rin_eating));
                scene.add_sequence(ks::DialogItem("rin", "Так что, Накай, какими судьбами ты здесь оказался?"));
                scene.add_sequence(ks::DialogItem("hi", "Как я уже сказал, мне поручили поискать некоторые вещи."));
                scene.add_sequence(ks::MusicStopItem());
                scene.add_sequence(ks::DialogItem("rin", "Нет, в школе. Судя по внешнему виду, ты вроде здоров. Проблема где-то внутри?"));
                scene.add_sequence(ks::DialogItem("", "Я в полном замешательстве, не могу из себя ничего выдавить."));
                scene.add_sequence(ks::DialogItem("hi", "Я…"));
                scene.add_sequence(ks::DialogItem("rin", "Попробую угадать. Я хорошо угадываю. Лучше, чем большинство людей."));
                scene.add_sequence(ks::DialogItem("", "Рин обрывает меня на полуслове прежде, чем я успеваю ответить на вопрос или как-то его обойти. Не знаю, что бы я выбрал."));
                scene.add_sequence(ks::DialogItem("", "Я снова оказываюсь перед этой проблемой. Я ещё никому не рассказывал здесь о своём недуге. Впрочем, возможно, лишь потому, что об этом никто не спрашивал."));
                scene.add_sequence(ks::DialogItem("", "У меня сложилось впечатление, что игнорирование таких вопросов – часть здешних общественных норм, как сказал учитель."));
                scene.add_sequence(ks::DialogItem("", "Интересно, могут ли люди здесь найти общий язык? Наверное, не легче, чем обычные люди."));
                scene.add_sequence(ks::DialogItem("", "Я не могу понять условия, в которых находятся Сидзунэ или Лилли."));
                scene.add_sequence(ks::BackgroundItem(bn::regular_bg_items::school_classroomart));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::rin, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("", "Разумеется, пока я об этом размышляю, Рин с глубокомысленным выражением лица продолжает обдумывать, что со мной не так."));
                scene.add_sequence(ks::DialogItem("", "Она помещает вилку в рот и откидывается назад, устремив взгляд в потолок, как если бы там был написан ответ."));
                scene.add_sequence(ks::DialogItem("", "Луч света из окна освещает её лицо, создавая на другой его половине маску тени."));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::rin, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("rin", "Вряд ли дело в твоей голове, а какая-нибудь кишечная проблема была бы банально-скучной, как мой обед. Да вдобавок пресной."));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::rin, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("rin", "Должно быть, проблема у тебя в штанах!"));
                scene.add_sequence(ks::MusicItem(bn::music_items::music_rin));
                scene.add_sequence(ks::DialogItem("", "Такое утверждение в духе пьяного Шерлока Холмса, а также полное отсутствие такта, с которым это произносится, застаёт меня врасплох."));
                scene.add_sequence(ks::DialogItem("", "Наверное, я неосознанно отшатываюсь, потому что глаза Рин расширяются от изумления и восторга."));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::rin, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("rin", "Так, значит, я права! У тебя что-то не в порядке с причиндалами?"));
                scene.add_sequence(ks::DialogItem("", "Всё ещё в шоке, но осознавая необходимость как-то отреагировать, я выдаю первое, что приходит в голову."));
                scene.add_sequence(ks::DialogItem("hi", "Нет! Ничего подобного! У меня проблемы с сердцем. Аритмия."));
                scene.add_sequence(ks::DialogItem("", "…"));
                scene.add_sequence(ks::DialogItem("", "Я произнёс это. Скорее, конечно, выпалил, но всё же сказал вслух."));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::rin, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("", "Девушка кривит губки и сердито на меня смотрит, будучи явно очень расстроенной."));
                scene.add_sequence(ks::DialogItem("rin", "Как скучно. Проблема в штанах была бы куда более позорной."));
                scene.add_sequence(ks::DialogItem("", "Что это за реакция?"));
                scene.add_sequence(ks::DialogItem("hi", "Извини, что подвёл."));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::rin, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("rin", "Я тебя прощаю. Просто я коллекционирую людей, и иметь человека с… ну, знаешь, такой проблемой было бы очень здорово."));
                scene.add_sequence(ks::DialogItem("hi", "Коллекционируешь людей?"));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::rin, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("rin", "Людей с разными проблемами."));
                scene.add_sequence(ks::DialogItem("hi", "Ты что, просто… ходишь и спрашиваешь у всех, что с ними не так?"));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::rin, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("rin", "Именно."));
                scene.add_sequence(ks::DialogItem("hi", "Понятно."));
                scene.add_sequence(ks::DialogItem("", "…"));
                scene.add_sequence(ks::MusicStopItem());
                scene.add_sequence(ks::DialogItem("", "Разговор затухает, и Рин возобновляет приём пищи, а я продолжаю думать над тем, что она сказала."));
                scene.add_sequence(ks::DialogItem("", "Я впервые поведал кому-то о своём недуге. Все остальные либо уже знали, либо слышали о нём от кого-то ещё."));
                scene.add_sequence(ks::DialogItem("", "Или же им до настоящего момента необязательно было знать об этом, как и всем прочим ученикам этой школы."));
                scene.add_sequence(ks::DialogItem("", "Мне следует упоминать его при знакомстве? От меня этого ожидают?"));
                scene.add_sequence(ks::DialogItem("", "«Привет, меня зовут Хисао. У меня серьёзные проблемы с сердцем»."));
                scene.add_sequence(ks::DialogItem("", "Так мне теперь представляться людям?"));
                scene.add_sequence(ks::DialogItem("", "Как будто наши болезни характеризуют нас. Что за мерзкая идея?"));
                scene.add_sequence(ks::DialogItem("", "Или, может быть, у этой Тедзуки просто странный интерес к подобным вещам?"));
                scene.add_sequence(ks::MusicItem(bn::music_items::music_dreamy));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::bg, ks::SpriteEventType::None));
