                scene.add_sequence(ks::DialogItem("", "Конечно нет, но такая постановка вопроса вызывает ощущение, что я должен был с самого первого дня прямо-таки надрываться на беговых дорожках."));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::rin, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("hi", "У вас есть люди, которые шпионят за мной?"));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::nurse, ks::SpriteEventType::None));
