                scene.add_sequence(ks::DialogItem("nk", "Не совсем. Просто я со многими знаком. Но у нас другая тема разговора, не пытайся от неё увильнуть."));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::rin, ks::SpriteEventType::None));
                scene.add_sequence(ks::DialogItem("hi", "Ну, я вот как раз выполнял импровизированные упражнения – подъём тяжестей."));
                scene.add_sequence(ks::DialogItem("", "Я несколько раз поднимаю и опускаю банку, изображая жалкую пародию на культуриста, хоть ручка и врезается болезненно мне в ладонь."));
                // scene.add_sequence(ks::SpriteItem(bn::sprite_items::nurse, ks::SpriteEventType::None));
